package com.icss.dkgy.entity;

public class AdminEntity {
	private int ad_id;  //用户id
	private String ad_name;  //用户姓名
	private String ad_pwd;  //用户密码
	private String ad_phone; //用户电话
	private String sex;		//性别
	private String imgpath; //邮箱
	private String status;
	
	
	/**
	 * 
	 */
	public AdminEntity() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	/**
	 * @param ad_name
	 * @param ad_pwd
	 */
	public AdminEntity(String ad_name, String ad_pwd) {
		super();
		this.ad_name = ad_name;
		this.ad_pwd = ad_pwd;
	}

	/**
	 * @param ad_id
	 * @param ad_name
	 * @param ad_pwd
	 * @param ad_phone
	 * @param sex
	 * @param imgpath
	 * @param status
	 */
	public AdminEntity(int ad_id, String ad_name, String ad_pwd, String ad_phone, String sex, String imgpath,
			String status) {
		super();
		this.ad_id = ad_id;
		this.ad_name = ad_name;
		this.ad_pwd = ad_pwd;
		this.ad_phone = ad_phone;
		this.sex = sex;
		this.imgpath = imgpath;
		this.status = status;
	}



	public String getStatus() {
		return status;
	}

	public void setStatus(String status) {
		this.status = status;
	}

	public int getAd_id() {
		return ad_id;
	}
	public void setAd_id(int ad_id) {
		this.ad_id = ad_id;
	}
	public String getAd_name() {
		return ad_name;
	}
	public void setAd_name(String ad_name) {
		this.ad_name = ad_name;
	}
	public String getAd_pwd() {
		return ad_pwd;
	}
	public void setAd_pwd(String ad_pwd) {
		this.ad_pwd = ad_pwd;
	}
	public String getAd_phone() {
		return ad_phone;
	}
	public void setAd_phone(String ad_phone) {
		this.ad_phone = ad_phone;
	}
	public String getSex() {
		return sex;
	}
	public void setSex(String sex) {
		this.sex = sex;
	}
	public String getImgpath() {
		return imgpath;
	}
	public void setImgpath(String imgpath) {
		this.imgpath = imgpath;
	}
	@Override
	public String toString() {
		return "AdminEntity [ad_id=" + ad_id + ", ad_name=" + ad_name + ", ad_pwd=" + ad_pwd + ", ad_phone=" + ad_phone
				+ ", sex=" + sex + ", imgpath=" + imgpath + "]";
	}
	
}
