package com.icss.dkgy.entity;

public class OwnerEntity {
	private int ownerid;  //用户id
	private String ownername;  //用户姓名
	private String ownerpwd;  //用户密码
	private String ownerphone; //用户电话
	private String ownersex;		//性别
	private String status;  //账户状态 0-正常  1-封号
	private String address;
	private String imgpath; //邮箱
	
	/**
	 * 
	 */
	public OwnerEntity() {
		super();
		// TODO Auto-generated constructor stub
	}
	/**
	 * @param ownerid
	 * @param ownername
	 * @param ownerpwd
	 * @param ownerphone
	 * @param ownersex
	 * @param status
	 * @param address
	 * @param imgpath
	 */
	public OwnerEntity(int ownerid, String ownername, String ownerpwd, String ownerphone, String ownersex,
			String status, String address, String imgpath) {
		super();
		this.ownerid = ownerid;
		this.ownername = ownername;
		this.ownerpwd = ownerpwd;
		this.ownerphone = ownerphone;
		this.ownersex = ownersex;
		this.status = status;
		this.address = address;
		this.imgpath = imgpath;
	}
	public int getOwnerid() {
		return ownerid;
	}
	public void setOwnerid(int ownerid) {
		this.ownerid = ownerid;
	}
	public String getOwnername() {
		return ownername;
	}
	public void setOwnername(String ownername) {
		this.ownername = ownername;
	}
	public String getOwnerpwd() {
		return ownerpwd;
	}
	public void setOwnerpwd(String ownerpwd) {
		this.ownerpwd = ownerpwd;
	}

	public String getOwnerphone() {
		return ownerphone;
	}
	public void setOwnerphone(String ownerphone) {
		this.ownerphone = ownerphone;
	}
	public String getOwnersex() {
		return ownersex;
	}
	public void setOwnersex(String ownersex) {
		this.ownersex = ownersex;
	}
	public String getStatus() {
		return status;
	}
	public void setStatus(String status) {
		this.status = status;
	}
	public String getAddress() {
		return address;
	}
	public void setAddress(String address) {
		this.address = address;
	}
	public String getImgpath() {
		return imgpath;
	}
	public void setImgpath(String imgpath) {
		this.imgpath = imgpath;
	}
	@Override
	public String toString() {
		return "OwnerEntity [ownerid=" + ownerid + ", ownername=" + ownername + ", ownerpwd=" + ownerpwd
				+ ", ownerphone=" + ownerphone + ", ownersex=" + ownersex + ", status=" + status + ", address="
				+ address + ", imgpath=" + imgpath + "]";
	}

	
	
	

}
